						$comment = $val['snippet']['topLevelComment']['snippet']['textOriginal'];
						//echo $comment.'<br>';
						$regex = "@(https?://([-\w\.]+[-\w])+(:\d+)?(/([\w/_\.#-]*(\?\S+)?[^\.\s])?).*$)@";
						$comment = preg_replace($regex, ' ', $comment);
						$comment = str_replace(array("'", "\"", "\n", "&quot;", "<br />"), "", htmlspecialchars($comment));
						$comment = remove_emoji($comment);
						//if(strlen($comment) > 80) $comment = substr($comment, -80);
						//echo $comment.'<br><br><br><br><br>';
    					if(strlen($comment) < 2)continue;
    					$comms = $comms.'"'.$comment.'",';
					}
					$comms=rtrim($comms,", ");
					$comms = $comms."]";
					//$comms = '["this is very good film", "I hate this film", "exciting movie and good actors"]';
					$result = post_request($comms);

					//echo $result.'<br><br>';
					$obj2 = json_decode($result, true);

					$pos = 0;
					$neg = 0;
					$neu = 0;
					$comment = [];
					$counter = 0;
					$clas = [];
					foreach ($obj2['comments'] as $val) {
						//echo $val['Comment'].'<br><br>';
						$comment[$counter] = $val['Comment'];
						$clas[$counter] = $val['Class'];
						if ($clas[$counter] == "1")$pos += 1;
						else if($clas[$counter] == "0")$neu += 1;
						else $neg += 1;
						$counter += 1;	
					}

					//echo 'Positive: '.$pos.' --- Negative: '.$neg. ' --- Neutral: '. $neu.'<br><br>';
				}else{
    				echo "<h2> <font color = 'red'> Wrong Video ID </font> </h2>";
				}

